import React, { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, FlatList, Image } from 'react-native';
import { gStyle } from '../styles/style';

export default function Main({ navigation }) {

    const [news, setNews] = useState([
        { key: "1", name: "Google", anons: "Google anons", full: "Google is cool!", img: "https://klike.net/uploads/posts/2019-01/1547365376_1.jpg" },
        { key: "2", name: "Apple", anons: "Apple anons", full: "Apple is cool!", img: "https://bipbap.ru/wp-content/uploads/2017/04/priroda_kartinki_foto_03.jpg" },
        { key: "3", name: "GitHub", anons: "GitHub anons", full: "GitHub is cool!", img: "https://vjoy.cc/wp-content/uploads/2020/10/dlya_dushi_35_13130628.jpg" },
    ])

    return (
        <View style={gStyle.main}>
            <Text style={[gStyle.title, styles.header]}>Main Page</Text>
            <FlatList
                data={news}
                renderItem={({ item }) => (
                    <TouchableOpacity style={styles.item} onPress={() => navigation.navigate('FullInfo', item)}>
                        <Image style={styles.image} source={{ uri: item.img }} />
                        <Text style={styles.title}>{item.name}</Text>
                        <Text style={styles.anons}>{item.anons}</Text>
                    </TouchableOpacity>
                )}
            />
        </View>
    );
}
const styles = StyleSheet.create({
    header: {
        marginBottom: 30,
        marginTop: 20
    },
    item: {
        width: '100%',
        marginBottom: 30,

    },
    title: {
        fontFamily: 'mt-bold',
        fontSize: 22,
        textAlign: 'center',
        marginTop: 20,
        color: '#474747'
    },
    anons: {
        fontFamily: 'mt-light',
        fontSize: 16,
        textAlign: 'center',
        marginTop: 5,
        color: '#474747'
    },
    image: {
        width: '100%',
        height: 200,
    }
})
