import React, { useState } from 'react';
import { Text, View, TouchableOpacity, FlatList } from 'react-native';
import { gStyle } from '../styles/style';


export default function Main({ navigation }) {

    const [news, setNews] = useState([
        { key: "1", name: "Google", anons: "Google anons", full: "Google is cool!" },
        { key: "2", name: "Apple", anons: "Apple anons", full: "Apple is cool!" },
        { key: "3", name: "GitHub", anons: "GitHub anons", full: "GitHub is cool!" },
    ])

    return (
        <View style={gStyle.main}>
            <Text style={gStyle.title}>Main Page</Text>
            <FlatList
                data={news}
                renderItem={({ item }) => (
                    <TouchableOpacity onPress={() => navigation.navigate('FullInfo', item)}>
                        <Text>{item.name}</Text>
                        <Text>{item.anons}</Text>
                    </TouchableOpacity>
                )}
            />

        </View>
    );

}
